mapper.select:
SELECT big_type AS one_key,payment_type AS two_key, key_name AS two_value FROM payment_dict
WHERE is_income = #{isIncome}
ORDER BY big_type

SELECT pd.payment_type, pd.key_name, pd.is_income, big_type, pd.color,COUNT(pr.record_id) AS recordNum,pbt.key_name AS bigTypeName,pbt.color AS bigTypeColor,pbt.type_desc FROM payment_dict pd LEFT JOIN payment_record pr on pd.payment_type = pr.payment_type LEFT OUTER JOIN pay_big_type pbt on pbt.typeKey = pd.big_type
pd.big_type = #{dto.bigType}
pd.is_income = #{dto.isIncome}
GROUP BY pd.payment_type,key_name, pd.is_income, big_type, pd.color,bigTypeName,bigTypeColor ORDER BY big_type,pd.payment_type
