mapper.select:
SELECT pr.record_id, pr.payment_type, pr.is_income, pr.money, pr.pay_date, pr.remark,
pd.key_name,pd.color
FROM payment_record pr
LEFT JOIN payment_dict pd on pr.payment_type = pd.payment_type


        AND pd.big_type = #{bigType}


        AND pr.pay_date >= #{beginDate}


        AND pr.pay_date <= #{endDate}


ORDER BY pr.pay_date,pr.record_id

SELECT pay_date,
       COUNT(*)                         as pay_count,
       SUM(IF(is_income = 0, money, 0)) AS money_out,
       SUM(IF(is_income = 1, money, 0)) AS money_in
FROM payment_record
WHERE YEAR(pay_date) = #{year}
  AND MONTH(pay_date) = #{month}
GROUP BY pay_date
ORDER BY pay_date;

SELECT big_type, SUM(money) AS money FROM payment_record pd LEFT JOIN payment_dict p on pd.payment_type = p.payment_type WHERE pd.is_income = 0 AND YEAR(pay_date) = #{year}
AND MONTH(pay_date) = #{month}
GROUP BY big_type ORDER BY money

SELECT SUM(money)
FROM payment_record
WHERE is_income = 0
AND YEAR(pay_date) = #{year}

    AND MONTH(pay_date) = #{month}
