mapper.update:
UPDATE appointment
SET is_del=#{delDate}
WHERE appointment_id = #{appointmentId};

UPDATE appointment
SET is_del=#{delDate}
WHERE appointment_id IN

    #{appointmentId}

mapper.select:
select count(*) FROM appointment a, client b, pet c, department d, doctor e WHERE a.is_del = '0' AND a.client_id = b.client_id AND a.pet_id = c.pet_id AND a.department_id = d.department_id AND a.doctor_id = e.doctor_id
and b.client_name like #{seaName}
and c.pet_Name like #{seaName}
and e.doctor_name like #{seaName}
and d.department_name like #{seaName}

SELECT appointment_id, a.client_id, a.pet_id, appointment_date, a.department_id, a.doctor_id, appointment_info, a.create_time, b.client_name, c.pet_name, d.department_name, e.doctor_name FROM appointment a, client b, pet c, department d, doctor e WHERE a.is_del = '0' AND a.client_id = b.client_id AND a.pet_id = c.pet_id AND a.department_id = d.department_id AND a.doctor_id = e.doctor_id
and b.client_name like #{seaName}
and c.pet_Name like #{seaName}
and e.doctor_name like #{seaName}
and d.department_name like #{seaName}
order by appointment_date desc limit #{begin},#{pageItem};
